import { trigger, state, style, animate, transition } from '@angular/animations';
export const hideAnimation = trigger('hideAnimation', [state('opened', style({
  transform: 'translateX(0%)'
})), state('closed', style({
  transform: 'translateX(-100%)'
})), transition('* => *', [animate(500)])]);
export const contentAnimation = trigger('contentAnimation', [state('opened', style({
  marginLeft: '{{size}}'
}), {
  params: {
    size: '*'
  }
}), state('closed', style({
  marginLeft: '50px'
})), transition('* => *', [animate(500)])]);
export const menuAnimation = trigger('menuAnimation', [state('opened', style({
  opacity: 1,
  width: 'auto'
})), state('closed', style({
  opacity: 0,
  width: '0px'
})), transition('* => *', [animate(300)])]);
